function longestPalindrome(s) {
    if (s.length <= 1)
    return s;
    let longest = s[0];
    for (var i = 0; i < s.length; i++) {
        let temps = ""
        for (var j = i; j < s.length; j++) {
            temps += s[j];
            if (isPalindrome(temps)) {
                if (temps.length > longest.length)
                    longest = temps;
            }
        }

    }
    return longest;
}


function isPalindrome(s) {
    if (s.length < 2)
        return false;
    let ss = "";
    for (let index = s.length - 1; index >= 0; index--) {
        ss += s[index];
    }
    if (ss == s) {
        return true;
    }
    return false;
}
